mapper.select:
SELECT serial_number AS serialNumber, primitive_sn AS primitiveSn, batch_number batchNumber, TABLE, DATE_FORMAT( create_time, '%Y-%m-%d %H:%i' )createTime, staff, level, type, wave1, il1, wave2, il2, ref_1 ref1, ref_2 ref2, c.name config FROM t_il il left join wdm_config c on il.config=c.id where 1=1
and serial_number = #{serialNumber}
and batch_number = #{batchNumber}
and level = #{level}
and staff=#{staff}
and create_time between #{beginTime} and #{endTime}
order by create_time desc
